    private void btnSearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSearchActionPerformed
        String output = "";
        int searchAccountNo = Integer.parseInt(tfSearchAccountNo.getText());
        Owner owr = operation.searchRecord(searchAccountNo);
        
        if(owr == null)
            ta.setText("Sorry, the record does not exist");
        else{
            JOptionPane.showMessageDialog(this, "The record of this account " + searchAccountNo + " is found.");
            tfSearchAccountNo.setText("");
            output = "Account No: " + owr.getAccountNo() + "\nBill Date: " + owr.getBillDate() + "\nName: " + owr.getOwnerName() + "\nAddress: " + owr.getOwnerAddress() + "\nArrears: RM" + owr.getArrears()
                    + "\nPrevious Meter Reading: " + owr.getPreviousMeterReading()+ "\nCurrent Meter Reading: " + owr.getCurrentMeterReading() + "\nTotal Unit (kWh): "
                    + owr.getTotalUnit() + "\nCurrent Charge (RM): " + owr.getCurrentCharge() + "\nTotal Bill (RM): " + owr.getTotalBill();
            ta.setText(output);
            tfAccountNo.setText(owr.getAccountNo());
            tfOwnerName.setText(owr.getOwnerName());
            tfAddress.setText(owr.getOwnerAddress());
            tfArrears.setText(owr.getArrears());
            tfBillDate.setText(owr.getBillDate());
            tfPreviousReading.setText(owr.getPreviousMeterReading());
            tfCurrentReading.setText(owr.getCurrentMeterReading());
            tfTotalUnit.setText(owr.getTotalUnit());
            tfCurrentCharge.setText(owr.getCurrentCharge());
            tfTotalBill.setText(owr.getTotalBill());           
        }//else
    }//GEN-LAST:event_btnSearchActionPerformed

