    private void btnEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditActionPerformed
        int accountNo = Integer.parseInt(tfAccountNo.getText());
        String billDate = tfBillDate.getText();
        String ownerName = tfOwnerName.getText();
        String ownerAddress = tfAddress.getText();
        double arrears = Double.parseDouble(tfArrears.getText());
        int previousMeterReading = Integer.parseInt(tfPreviousReading.getText());
        int currentMeterReading = Integer.parseInt(tfCurrentReading.getText());
        int totalUnit = Integer.parseInt(tfTotalUnit.getText());
        double currentCharge = Double.parseDouble(tfCurrentCharge.getText());
        double totalBill = Double.parseDouble(tfTotalBill.getText());
       
        ta.setText("Account No: " + accountNo + "\nBill Date: " + billDate + "\nName: " + ownerName + "\nAddress: " + ownerAddress + "\nArrears: RM" + arrears
                   + "\nPrevious Meter Reading: " + previousMeterReading + "\nCurrent Meter Reading: " + currentMeterReading + "\nTotal Unit (kWh): " 
                   + totalUnit + "\nCurrent Charge (RM): " + currentCharge + "\nTotal Bill: " + totalBill);
        operation.updateRecord(owner);
        JOptionPane.showMessageDialog(this, "The record of this account " + accountNo + " is edited successfully.");
    }//GEN-LAST:event_btnEditActionPerformed

    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
        int searchAccountNo = Integer.parseInt(tfAccountNo.getText());
        operation.deleteRecord(searchAccountNo);
        JOptionPane.showMessageDialog(this, "The record of this account " + searchAccountNo + " is deleted successfully.");
    }//GEN-LAST:event_btnDeleteActionPerformed

